pragma solidity 0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "hardhat/console.sol";

/**
 * @title DEX Template
 * @author stevepham.eth and m00npapi.eth
 * @notice Empty DEX.sol that just outlines what features could be part of the challenge (up to you!)
 * @dev We want to create an automatic market where our contract will hold reserves of both ETH and 🎈 Balloons. These reserves will provide liquidity that allows anyone to swap between the assets.
 * NOTE: functions outlined here are what work with the front end of this challenge. Also return variable names need to be specified exactly may be referenced (It may be helpful to cross reference with front-end code function calls).
 */
contract DEX {
	/* ========== GLOBAL VARIABLES ========== */
	uint256 public totalLiquidity; //of ETH
	mapping(address => uint256) liquidity; //Mapping of address to ETH
	IERC20 token; //instantiates the imported contract

	/* ========== EVENTS ========== */

	/**
	 * @notice Emitted when ethToToken() swap transacted
	 */
	event EthToTokenSwap(
		address swapper,
		uint256 tokenOutput,
		uint256 ethInput
	);

	/**
	 * @notice Emitted when tokenToEth() swap transacted
	 */
	event TokenToEthSwap(
		address swapper,
		uint256 tokensInput,
		uint256 ethOutput
	);

	/**
	 * @notice Emitted when liquidity provided to DEX and mints LPTs.
	 */
	event LiquidityProvided(
		address liquidityProvider,
		uint256 liquidityMinted,
		uint256 ethInput,
		uint256 tokensInput
	);

	/**
	 * @notice Emitted when liquidity removed from DEX and decreases LPT count within DEX.
	 */
	event LiquidityRemoved(
		address liquidityRemover,
		uint256 liquidityWithdrawn,
		uint256 tokensOutput,
		uint256 ethOutput
	);

	/* ========== CONSTRUCTOR ========== */

	constructor(address token_addr) {
		token = IERC20(token_addr); //specifies the token address that will hook into the interface and be used through the variable 'token'
	}

	/* ========== MUTATIVE FUNCTIONS ========== */

	/**
	 * @notice initializes amount of tokens that will be transferred to the
	 *  DEX itself from the erc20 contract mintee (and only them based on how Balloons.sol is written).
	 *  Loads contract up with both ETH and Balloons.
	 * @param tokens amount to be transferred to DEX
	 * @return totalLiquidity is the number of LPTs minting as a result of deposits made to DEX contract
	 * NOTE: since ratio is 1:1, this is fine to initialize the totalLiquidity
	 *  (wrt to balloons) as equal to eth balance of contract.
	 */
	function init(uint256 tokens) public payable returns (uint256) {
		if (tokens < 0 || totalLiquidity != 0)
			revert("DEX: Invalid tokens value");
		totalLiquidity = address(this).balance;
		liquidity[msg.sender] = totalLiquidity;
		//token.approve(address(this), tokens);
		if (token.transferFrom(msg.sender, address(this), tokens)) {
			return totalLiquidity;
		}
		revert("DEX: Token transfer failed");
	}

	/**
	 * @notice returns yOutput, or yDelta for xInput (or xDelta)
	 * @dev Follow along with the [original tutorial](https://medium.com/@austin_48503/%EF%B8%8F-minimum-viable-exchange-d84f30bd0c90)
	 * Price section for an understanding of the DEX's pricing model and for a price function to add
	 * to your contract. You may need to update the Solidity syntax
	 * (e.g. use + instead of .add, * instead of .mul, etc). Deploy when you are done.
	 */
	function price(
		uint256 xInput,
		uint256 xReserves,
		uint256 yReserves
	) public pure returns (uint256 yOutput) {
		uint256 xInputWithFee = xInput * 997;
		uint256 numerator = xInputWithFee * yReserves;
		uint256 denominator = (xReserves * 1000) + xInputWithFee;
		yOutput = numerator / denominator;
	}

	/**
	 * @notice returns liquidity for a user.
	 * NOTE: this is not needed typically due to the `liquidity()` mapping variable
	 * being public and having a getter as a result. This is left though as it is
	 * used within the front end code (App.jsx).
	 * NOTE: if you are using a mapping liquidity, then you can use `return liquidity[lp]`
	 * to get the liquidity for a user.
	 * NOTE: if you will be submitting the challenge make sure to implement this function
	 * as it is used in the tests.
	 */
	function getLiquidity(address lp) public view returns (uint256) {
		return liquidity[lp];
	}

	/**
	 * @notice sends Ether to DEX in exchange for $BAL
	 */
	function ethToToken() public payable returns (uint256 tokenOutput) {
		require(msg.value > 0, "cannot swap 0 ETH");
		uint256 ethLiquidity = address(this).balance - msg.value;
		uint256 tokenReserve = token.balanceOf(address(this));
		tokenOutput = price(msg.value, ethLiquidity, tokenReserve);
		console.logUint(tokenOutput);
		if (token.transfer(msg.sender, tokenOutput))
			emit EthToTokenSwap(msg.sender, tokenOutput, msg.value);
		else revert("ethToToken(): reverted swap.");
		return tokenOutput;
	}

	/**
	 * @notice sends $BAL tokens to DEX in exchange for Ether
	 */
	function tokenToEth(uint256 tokenInput) public returns (uint256 ethOutput) {
		require(tokenInput > 0, "cannot swap 0 tokens");
		uint256 ethLiquidity = address(this).balance;
		ethOutput = price(tokenInput, totalLiquidity, ethLiquidity);
		require(
			token.transferFrom(msg.sender, address(this), tokenInput),
			"tokenToEth(): reverted swap."
		);
		(bool success, ) = msg.sender.call{ value: ethOutput }("");
		if (success) emit TokenToEthSwap(msg.sender, tokenInput, ethOutput);
		else revert("tokenToEth: revert in transferring eth to you!");
		return ethOutput;
	}

	/**
	 * @notice allows deposits of $BAL and $ETH to liquidity pool
	 * NOTE: parameter is the msg.value sent with this function call.
	 * That amount is used to determine the amount of $BAL needed as well and taken from the depositor.
	 * NOTE: user has to make sure to give DEX approval to spend their
	 * tokens on their behalf by calling approve function prior to this function call.
	 * NOTE: Equal parts of both assets will be removed from the user's
	 * wallet with respect to the price outlined by the AMM.
	 */
	function deposit() public payable returns (uint256 tokensDeposited) {
		require(msg.value > 0, "Must send value when depositing");
		uint256 ethReserve = address(this).balance - msg.value;
		uint256 tokenReserve = token.balanceOf(address(this));
		uint256 tokenDeposit;

		tokenDeposit = ((msg.value * tokenReserve) / ethReserve) + 1;

		uint256 liquidityMinted = (msg.value * totalLiquidity) / ethReserve;
		liquidity[msg.sender] += liquidityMinted;
		totalLiquidity += liquidityMinted;

		require(token.transferFrom(msg.sender, address(this), tokenDeposit));
		emit LiquidityProvided(
			msg.sender,
			liquidityMinted,
			msg.value,
			tokenDeposit
		);
		return tokenDeposit;
	}

	/**
	 * @notice allows withdrawal of $BAL and $ETH from liquidity pool
	 * NOTE: with this current code, the msg caller could end up getting
	 * very little back if the liquidity is super low in the pool. I guess they could see that with the UI.
	 */
	function withdraw(
		uint256 amount
	) public returns (uint256 eth_amount, uint256 token_amount) {
		require(
			liquidity[msg.sender] >= amount,
			"withdraw: sender does not have enough liquidity to withdraw."
		);
		uint256 ethReserve = address(this).balance;
		uint256 tokenReserve = token.balanceOf(address(this));
		uint256 ethWithdrawn;

		ethWithdrawn = (amount * ethReserve) / totalLiquidity;

		uint256 tokenAmount = (amount * tokenReserve) / totalLiquidity;
		liquidity[msg.sender] -= amount;
		totalLiquidity -= amount;
		(bool sent, ) = payable(msg.sender).call{ value: ethWithdrawn }("");
		require(sent, "withdraw(): revert in transferring eth to you!");
		require(token.transfer(msg.sender, tokenAmount));
		emit LiquidityRemoved(msg.sender, amount, tokenAmount, ethWithdrawn);
		return (ethWithdrawn, tokenAmount);
	}
}
